// Generated by CoffeeScript 1.12.7
var reset;

reset = function() {
  $('#message').html('');
  $('#compressed').removeClass('error');
  $('#ordered').removeClass('error');
};

$().ready(function() {
  $('#compressize').on('click', function() {
    var e, json, jsonArray, jsonStr;
    reset();
    json = $('#ordered').val();
    try {
      jsonArray = JSON.parse(json);
    } catch (error) {
      e = error;
      $('#message').html('JSONが不正です。');
      $('#ordered').addClass('error');
      return false;
    }
    jsonStr = JSON.stringify(jsonArray, null, 0);
    $('#compressed').val(jsonStr);
  });
  $('#orderize').on('click', function() {
    var e, json, jsonArray, jsonStr;
    reset();
    json = $('#compressed').val();
    try {
      jsonArray = JSON.parse(json);
    } catch (error) {
      e = error;
      $('#message').html('JSONが不正です。');
      $('#compressed').addClass('error');
      return false;
    }
    jsonStr = JSON.stringify(jsonArray, null, 4);
    $('#ordered').val(jsonStr.replace(/\\n/g, "\n"));
  });
  $('#selectCompressed').on('click', function() {
    $('#compressed').select();
  });
  $('#selectOrdered').on('click', function() {
    $('#ordered').select();
  });
});
